// scripts (main.js)

//  Flickity.setJQuery()
// make Flickity a jQuery plugin
Flickity.setJQuery($);
jQueryBridget('flickity', Flickity, $);
// now you can use $().flickity()
$('.carousel-main').flickity({
  pageDots: false,
  prevNextButtons: false,
  wrapAround: true
});
$('.carousel-nav').flickity({
  asNavFor: '.carousel-main',
  pageDots: false,
  wrapAround: true,
  cellAlign: 'left',
  arrowShape: {
    x0: 10,
    x1: 60,
    y1: 40,
    x2: 60,
    y2: 30,
    x3: 20
  }
});

// svg for everybody
$(document).ready(function () {
  svg4everybody({});
});

// scrollbar plugin
$(window).on('load', function () {
  $('.terms-conditions-list').mCustomScrollbar({
    setHeight: '500px',
    theme: 'light-2'
  });
});

// show the terms list on click

$('.terms-conditions').on('click', function () {
  $('.terms-conditions-list').toggle(400, 'linear', function () {
    $('.terms-conditions').toggleClass('bg-grey');
    $('.close').toggleClass('is-visible');
  });
});
